import { Link } from '../graph';
import { TextureInfo } from './texture-info';
import { TextureSampler } from './texture-sampler';
/** @hidden */
export class TextureLink extends Link {
    constructor() {
        super(...arguments);
        this.textureInfo = new TextureInfo();
        this.sampler = new TextureSampler();
    }
    copy(other) {
        this.textureInfo.copy(other.textureInfo);
        this.sampler.copy(other.sampler);
        return this;
    }
}
/** @hidden */
export class AttributeLink extends Link {
    constructor() {
        super(...arguments);
        this.semantic = '';
    }
    copy(other) {
        this.semantic = other.semantic;
        return this;
    }
}
/** @hidden */
export class IndexLink extends Link {
    copy(other) { return this; }
}
