/** @hidden */
export class MathUtils {
    static identity(v) {
        return v;
    }
    static denormalize(c, componentType) {
        switch (componentType) {
            case 5126 /* FLOAT */:
                return c;
            case 5123 /* UNSIGNED_SHORT */:
                return c / 65535.0;
            case 5121 /* UNSIGNED_BYTE */:
                return c / 255.0;
            case 5122 /* SHORT */:
                return Math.max(c / 32767.0, -1.0);
            case 5120 /* BYTE */:
                return Math.max(c / 127.0, -1.0);
        }
    }
    static normalize(f, componentType) {
        switch (componentType) {
            case 5126 /* FLOAT */:
                return f;
            case 5123 /* UNSIGNED_SHORT */:
                return Math.round(f * 65535.0);
            case 5121 /* UNSIGNED_BYTE */:
                return Math.round(f * 255.0);
            case 5122 /* SHORT */:
                return Math.round(f * 32767.0);
            case 5120 /* BYTE */:
                return Math.round(f * 127.0);
        }
    }
}
